    private fun dynamicJSONTextView(obj: JSONObject, rootV: LinearLayout, keyExpected: String?) {
        val key: String
        val value: String
        if (keyExpected == null) {
            val keys = obj.names()
            for (i in 0 until obj.length()) {
                dynamicJSONTextView(obj, rootV, keys.getString(i)) // 재귀적 forEach로 모든 원소를 call
            }
            return // 모든 loop가 끝난 후 함수 종료
        } else {
            key = keyExpected!! // Not Null
            value = obj.getString(key) // key로 value 가지고오기
        }
        val usingLL = LinearLayout(this)
        usingLL.decorate()
        var isJsonObj: Boolean
        var isJsonArr: Boolean
        // 두번의 타입 체크
        try {
            JSONObject(value)
            isJsonObj = true
        } catch (e: org.json.JSONException) {
            isJsonObj = false
        }
        try {
            JSONArray(value)
            isJsonArr = true
        } catch (e: org.json.JSONException) {
            isJsonArr = false
        }
        if (isJsonObj) {
            val title = TextView(this)
            title.text = key
            title.decorate()
            dynamicJSONTextView(JSONObject(value), usingLL, null) // value에 있는 모든 원소를 call
            usingLL.invalidate()
        } else if (isJsonArr) {
            val LL = LinearLayout(this)
            LL.decorate()
            val arr = JSONArray(value)
            for (j in 0 until arr.length()) {
                val textV = TextView(this)
                textV.text = arr[j].toString()
                textV.decorate()
                LL.addView(textV)
                LL.invalidate()
            }
            usingLL.addView(LL)
            usingLL.invalidate()
        } else {
            val keyText = TextView(this)
            val valueText = TextView(this)
            val LL = LinearLayout(this)
            LL.decorate()
            keyText.text = key
            keyText.decorate()
            valueText.text = value
            valueText.decorate()
            LL.addView(keyText)
            LL.addView(valueText)
            LL.invalidate()
            usingLL.addView(LL)
            usingLL.invalidate()
        }
        rootV.addView(usingLL)
        rootV.invalidate()
    }
